with basic_bau as(select r.baunit_id, 		r.id as right_id, 		r.share, r.beginLifespan, 		r.endLifespan, 		st_union(geometry) as geometry  from spatial_unit as s join rrr as r  on s.baunit_id = r.baunit_idgroup by s.baunit_id),rights_and_parties as (select r.id as right_id, 		r.type as type, 		r.baunit_id, r.share, 		p.party_id,		p.name,		p."role",		p.type,		r."start" as starting_Datefrom rrr as r left join party as p on p.party_id=r.party_id)Select b.*, 	   r.party_id, 	   r.typeFrom basic_bau as b join rights_and_parties as r on r.right_id = b.right_id