-- QGIS Virtual layer --select 'BAU'||'-'||cast(row_number() over() as string) as id, 	   r.party_id,	   r.type as right_type,		   datetime() as starting_Date,	   st_union(s.geometry) as geometryfrom rrr as r join spatial_unit as s on r.spatial_unit = s.sunit_idgroup by r.party_id-- Geopackage view --Create view bau as select 'BAU'||'-'||cast(row_number() over() as string) as id, 	   r.party_id,	   r.type as right_type,		   datetime() as starting_Date,	   st_union(s.geom) as geomfrom rrr as r join spatial_unit as s on r.spatial_unit = s.sunit_idgroup by r.party_id